import java.util.Scanner; public class Planta{public static void main(String[] args) {Scanner sc = new Scanner(System.in);System.out.println("Introduce si la planta es o no es una suculenta(\"true or false\") ninguna otra palabra, y el número de días que la planta no ha sido regada");RegarPlanta(sc.nextBoolean(), sc.nextInt());}public static void RegarPlanta(boolean es_suculenta, int dias){if (es_suculenta == false && dias > 3) System.out.println("Momento de regar la planta.");else if (es_suculenta == true && dias <= 12) System.out.println("No regar la planta!");else if (es_suculenta == true && dias >= 13) System.out.println("Riega solo un poco la planta.");else System.out.println("No regar la planta!");}}
